    "# Función para procesar la descripción y obtener el número de obras por género\n",
    "\n",
    "def procesar_descripcion(descripcion):\n",
    "\n",
    "    # Inicializar un diccionario con el número de obras por género\n",
    "\n",
    "    obras_por_genero = {'history': 0, 'comedy': 0, 'poem': 0, 'tragedy': 0, 'sonnet': 0}\n",
    "\n",
    "    # Dividir la descripción en partes separadas por ', '\n",
    "\n",
    "    partes = descripcion.split(', ')\n",
    "\n",
    "    # Iterar sobre cada parte de la descripción\n",
    "\n",
    "    for parte in partes:\n",
    "\n",
    "        # Dividir la parte en la cantidad y el género\n",
    "\n",
    "        cantidad, genero = parte.split(' obras de ')\n",
    "\n",
    "        # Eliminar espacios en blanco alrededor del género\n",
    "\n",
    "        genero = genero.strip()\n",
    "\n",
    "        # Convertir la cantidad a entero y actualizar el diccionario\n",
    "\n",
    "        obras_por_genero[genero] += int(cantidad)  # Usar operador de suma +=\n",
    "\n",
    "    return obras_por_genero\n",
    "\n",
    "\n",
    "\n",
    "# DataFrame con los períodos y descripciones\n",
    "\n",
    "periodos_df = pd.DataFrame({\n",
    "\n",
    "    'Periodos': ['1588-1592', '1593-1597', '1598-1602', '1603-1607', '1608-1613'],\n",
    "\n",
    "    'Descripción': [\n",
    "\n",
    "        \"4 obras de history, 1 obras de comedy\",\n",
    "\n",
    "        \"5 obras de comedy, 4 obras de history, 2 obras de poem, 2 obras de tragedy\",\n",
    "\n",
    "        \"5 obras de comedy, 3 obras de tragedy, 2 obras de poem, 1 obras de history\",\n",
    "\n",
    "        \"6 obras de tragedy, 1 obras de comedy\",\n",
    "\n",
    "        \"3 obras de history, 2 obras de comedy, 1 obras de poem, 1 obras de sonnet\"\n",
    "\n",
    "    ]\n",
    "\n",
    "})\n",
    "\n",
    "\n",
    "\n",
    "# Procesar la descripción de cada período para obtener el número de obras por género\n",
    "\n",
    "periodos_df['Obras_por_Genero'] = periodos_df['Descripción'].apply(procesar_descripcion)\n",
    "\n",
    "\n",
    "\n",
    "# DataFrame con el número de obras por período y género\n",
    "\n",
    "df_generos_por_periodo = pd.DataFrame(periodos_df['Obras_por_Genero'].tolist(), index=periodos_df['Periodos'])\n",
    "\n",
    "\n",
    "\n",
    "# Graficar barras apiladas\n",
    "\n",
    "plt.figure(figsize=(10, 6))\n",
    "\n",
    "\n",
    "\n",
    "# Obtener los géneros disponibles\n",
    "\n",
    "generos = df_generos_por_periodo.columns\n",
    "\n",
    "\n",
    "\n",
    "# Inicializar la posición de la barra inferior\n",
    "\n",
    "bottom = None\n",
    "\n",
    "\n",
    "\n",
    "# Iterar sobre cada género para graficar las barras\n",
    "\n",
    "for genero in generos:\n",
    "\n",
    "    plt.bar(df_generos_por_periodo.index, df_generos_por_periodo[genero], label=genero, bottom=bottom)\n",
    "\n",
    "    if bottom is None:\n",
    "\n",
    "        bottom = df_generos_por_periodo[genero].values\n",
